('2142d74f-9247-f2f3-e9c4-c51c4b9fbab7', 1, '名字服务服务器配置', '名字服务服务器配置', 'undefined', '<script>\n  \nseajs.use(\'lib\', function(lib) {\n  $(document).on(BDY.click, \'#btn_reload\', function() {\n       var data = {\n        ip : $(this).attr(\'data-server_ip\'),\n        env : $(this).attr(\'data-env\')\n      };\n      var url = \'/nameService/restart\';\n      lib.post(url, data, function(objResult) {\n        if (objResult.result) {\n          lib.showTip(objResult.msg);\n        } else {\n          lib.showErrTip(objResult.msg);\n        }\n      }, {\n        loading : true\n      });\n  });\n  \n    $(document).on(BDY.click, \'#btn_echo\', function() {\n      var data = {\n        ip : $(this).attr(\'data-server_ip\'),\n        env : $(this).attr(\'data-env\')\n      };\n      var url = \'/nameService/echo\';\n      lib.post(url, data, function(objResult) {\n        if (objResult.result) {\n          setTimeout(function() {\n            $(\'#search\').trigger(BDY.click);\n          }, 1000);\n        } else {\n          lib.showErrTip(objResult.msg);\n        }\n      }, {\n        loading : true\n      });\n  });\n  \n});\n  \n</script>\n\n', 10, NULL, 'admin', '2017-09-21 07:52:07', '2017-10-18 16:30:45', 1, 'mysqli', '', '', 3306, 'ns_server', 'root', '', '', '', NULL, 6379, NULL, NULL, 86400, 0, 1, 0, NULL, 0, 0, 0, 'admin', 0, 'Web', 'adsystem', 'dw_ka:act', 0, 0, 0, '', 0, 'adsystem', '', '', 0, 0, 0, 0, 0, NULL),
