('62b2a8ed-2842-585a-d1ac-44c19f3543d6', 1, '名字服务配置', '名字服务配置', 'undefined', '<script>\n  \nseajs.use(\'lib\', function(lib) {\n  \n  // 发布到预发布\n  $(document).on(BDY.click, \'.js-publish_env\', function() {\n      var $tr = $(this).parent().parent().parent();\n      var url = \'/nameService/nsConfigEnv\';\n      var data = {};\n      data.env = $tr.find(\'[name=env]\').val();\n        data.node_name = $tr.find(\'[name=node_name]\').val();\n      data.targetEnv = $(this).attr(\'data-env\');\n      lib.post(url, data, function(objResult) {\n        if (objResult.result) {\n            lib.showTip(\'同步成功\');\n        }else{\n          lib.showErrorTip(objResult.msg);\n        }\n      }, {\n        loading : true\n      });\n  });\n  // 同步节点信息\n  $(document).on(BDY.click, \'.js-copy_node\', function() {\n    console.log(\'copy\');\n       var $tr = $(this).parent().parent().parent();\n      var url = \'/nameService/copyNsconfig\';\n      var data = {};\n     data.env = $tr.find(\'[name=env]\').val();\n        data.node_name = $tr.find(\'[name=node_name]\').val();\n      lib.post(url, data, function(objResult) {\n        if (objResult.result) {\n            lib.showTip(\'复制成功\');\n            $(\'#search\').trigger(BDY.click);\n        }else{\n          lib.showErrorTip(objResult.msg);\n        }\n      }, {\n        loading : true\n      });\n  });\n  \n  $(document).on(BDY.click, \'#btn_publish\', function() {\n       var $tr = $(this).parent().parent().parent();\n      var url = \'/nameService/publishDialog\';\n      lib.get(url, function(html) {\n        $(\'#operDiv\').html(html);\n      }, {\n        loading : true,\n        type : \'text\'\n      });\n  });\n\n  $(document).on(\'diy_load_table\', function() {\n    var html = \'<button type=\"button\" style=\"margin-left:10px;\" class=\"btn btn-danger btn-sm\" id=\"btn_publish\">\\\n            <i class=\"glyphicon glyphicon-cloud\"></i>发布\\\n        </button>\';\n    \n    $(\'#btn_add\').after(html);\n  });\n  \n  $(document).on(\'diy_edit_add_view\', function() {\n    setTimeout(function() {\n      _init(true);\n    }, 300);\n  });\n  $(document).on(\'diy_edit_save_view\', function() {\n    setTimeout(function() {\n      _init(false);\n    }, 300);\n  }); \n  \n  function _init(addFlag) {\n    $(\'.modal [name=node_type]\').on(\'change\', function() {\n      _hideAll();\n      var val = $(this).val();\n      if (val == \'string\') {\n        _showString();\n      } else if(val == \'code\') {\n        _showCode();\n      }else{\n        _showHashTable();\n        if (!addFlag) {\n          var node_name = $(\'#operDiv [name=node_name]\').val();\n          var env = $(\'#operDiv [name=env]:checked\').val();\n          _getData(node_name, env);\n        }\n      }\n    });\n\n    $(\'.modal [name=node_type]:checked\').trigger(\'change\');\n  }\n  \n  function _hideAll() {\n    $(\'[name=creator]\').parents(\'.form-group\').hide();\n    $(\'.modal [name=object_type]\').parents(\'.form-group\').hide();\n    $(\'.modal [name=node_value]\').parents(\'.form-group\').hide();\n    $(\'.modal [name=node_tips]\').parents(\'.form-group\').hide();\n    $(\'.modal [name=value_type]\').parents(\'.form-group\').hide();\n    \n    $(\'#objectInfo\').hide();\n  }\n  \n  function _showString() {\n    $(\'.modal [name=node_value]\').parents(\'.form-group\').show();\n    $(\'.modal [name=value_type]\').parents(\'.form-group\').show();\n  }\n  \n  function _showCode() {\n   $(\'.modal [name=node_value]\').parents(\'.form-group\').show();\n    $(\'.modal [name=node_tips]\').parents(\'.form-group\').show();\n    $(\'.modal [name=value_type]\').parents(\'.form-group\').show();\n  }\n\n  function _showHashTable() {\n    $(\'.modal [name=object_type]\').parents(\'.form-group\').show();\n    $(\'#objectInfo\').show();\n  }\n  \n  function _getData(node_name, env) {\n    var $objectType = $(\'.modal [name=object_type]\').parents(\'.form-group\');\n    var $objectInfo = $(\'#objectInfo\');\n    if ($objectInfo.length == 0) {\n      $objectInfo = $(\'<div id=\"objectInfo\" class=\"form-group\" style=\"display: block;\"><div class=\"col-sm-12\"></div></div>\');\n      $objectType.after($objectInfo);\n    }\n\n    var html = \"<iframe style=\'width:100%;height:480px;\' src=\'/DiyData/report?tableId=3740c87d-82f4-ddbb-96d6-a7554c694b66&_nodeId=6595&\"\n             + \"where=[[\\\"node_name\\\",\\\"=\\\",\\\"\" + node_name + \"\\\"],[\\\"env\\\",\\\"=\\\",\\\"\" + env + \"\\\"]]&keyWord={}\'></iframe>\";\n    $objectInfo.find(\'.col-sm-12\').html(html);\n  }\n  \n});\n  \n</script>', 10, NULL, 'admin', '2017-08-04 10:22:06', '2018-04-20 17:02:49', 1, 'mysqli', '', '', 3306, 'ns_node', 'root', '', '', 'Diy_Table::on(\'diy_edit_save:before\', function(&$args) { \n  $type = $args[\'type\'];\n  $oldData = $args[\'oldData\'];\n  $newData = $args[\'newData\'];\n  $nodeName = $newData[\'node_name\'];\n  $node_type = $newData[\'node_type\'];\n  $node_tips = $newData[\'node_tips\'];\n  if ($nodeName) {\n    $parts = explode(\':\', $nodeName);\n    $count = min(count($parts), 4);\n    for ($i = 1; $i < $count; $i++) {\n      $newData[\"dir_{$i}\"] = $parts[$i - 1];\n    }\n    $args[\'newData\'] = $newData;\n  }\n  \n  if ($type == \'add\' && $node_type == \'hash_table\') {\n    addHashData($args);\n  }\n  if($type == \'edit\' && $oldData[\'node_type\'] == \'hash_table\'){\n    saveHashData($args);\n  }\n  if($type == \'del\' && $oldData[\'node_type\'] == \'hash_table\'){\n   deleteHashData($args);\n  }\n  \n  return true;\n});\n\nfunction addHashData($args) { \n  $newData = $args[\'newData\'];\n  $node_name = $newData[\'node_name\'];\n  $type = $args[\'type\'];\n\n  switch ($newData[\'object_type\']) {\n    case \'db\':\n      $data = [\n        \'dbHost\' => [\'string\', \'0.0.0.0\'],\n        \'dbPort\' => [\'number\', 6301],\n        \'dbType\' => [\'string\', \'mysqli\'],\n        \'dbName\' => [\'string\', \'\'],\n        \'dbUser\' => [\'string\', \'\'],\n        \'dbPass\' => [\'string\', \'\'],\n        \'enable\' => [\'number\', 1],\n      ];\n      break;\n    case \'redis\':\n      $data = [\n        \'host\' => [\'string\', \'0.0.0.0\'],\n        \'port\' => [\'number\', 6301],\n        \'pwd\' => [\'string\', \'\'],\n        \'db\' => [\'number\', 0],\n        \'pwd\' => [\'string\', \'\'],\n        \'connect_timeout\' => [\'number\', 3000],\n      ];\n      break;\n    case \'r2m\':\n      $data = [\n        \'key\' => [\'string\', \'\'],\n        \'ttl\' => [\'number\', 600],\n      ];\n      break;\n     \n    default:\n      $data = [];\n      break;\n  } \n\n  $datas = [];\n  foreach ($data as $key_name => $value) {\n    $value_type = $value[0];\n    $key_value = $value[1];\n    $datas[] = compact(\'node_name\', \'key_name\', \'value_type\', \'key_value\');\n  }\n\n  if (count($datas) > 0) {\n    $objHashTable = new TableHelper(\'ns_hash_table\');\n    $objHashTable->addObjects2($datas);\n  }\n\n  return true;\n}\n\nfunction saveHashData($args) { \n    $newData = $args[\'newData\'];\n   if(!$newData[\'node_name\']){\n        return true;\n    }\n    $oldData = $args[\'oldData\'];\n    $new_node_name = $newData[\'node_name\'];\n    $old_node_name = $oldData[\'node_name\'];\n    $env = $oldData[\'env\'];\n\n    $objHashTable = new TableHelper(\'ns_hash_table\');\n    $_where = [\n        \'node_name\' => $old_node_name,\n        \'env\' => $env,\n    ];\n    $data = [\n        \'node_name\' => $new_node_name,\n        \'update_time\' => $newData[\'update_time\'],\n    ];\n    if($newData[\'enable\']){\n        $data[\'enable\'] = $newData[\'enable\'];\n    }\n  \n    $objHashTable->updateObject($data, $_where);\n\n    return true;\n}\n\nfunction deleteHashData($args){\n    $type = $args[\'type\'];\n    if($type != \'del\'){\n        return true;\n    }\n    $newData = $args[\'newData\'];\n    $node_name = $newData[\'node_name\'];\n    $env = $newData[\'env\'];\n    $_where = [\n        \'node_name\' => $node_name,\n        \'env\' => $env,\n    ];\n    $objHashTable = new TableHelper(\'ns_hash_table\');\n    $objHashTable->delObject($_where);\n    return true;\n}\n\nreturn true;', NULL, 6379, NULL, NULL, 86400, 0, 1, 0, NULL, 0, 0, 0, 'admin', 0, 'Web', 'adsystem', 'dw_ka:act', 0, 0, 0, '', 0, 'adsystem', '', '', 0, 0, 0, 0, 0, 0),
